INSERT INTO `joke` VALUES ('2670', '老张去市场买鱼，活鱼18元一斤，他嫌贵。\r\n老板说“死鱼8元。”\r\n老张问：“死鱼是怎么死的？”\r\n老板说：“没人买气死的！”');
